oda.query_construct:
?parameter_binding_ontology_class a <http://www.w3.org/2002/07/owl#Class> ;
	<http://www.w3.org/2000/01/rdf-schema#subClassOf> ?parameter_binding_ontology_parent_class ;
	<http://www.w3.org/2000/01/rdf-schema#label> ?parameter_binding_ontology_class_extracted_label .

?entityOutput <http://odahub.io/ontology#hasParameter> ?parameter_binding_output ;
	 <http://odahub.io/ontology#hasOutput> ?output_binding_output .

?parameter_binding_output <http://odahub.io/ontology#parameter_name> ?parameter_binding_output_name ;
	a ?parameter_binding_output_type ;
	<http://odahub.io/ontology#value> ?parameter_binding_output_value ;
	<http://odahub.io/ontology#isAlso> ?parameter_binding_output_type .

?output_binding_output <http://odahub.io/ontology#output_name> ?output_binding_output_name ;
	a ?output_binding_output_type ;
	<http://odahub.io/ontology#isAlso> ?output_binding_output_type .

?entityInput <http://odahub.io/ontology#hasParameter> ?parameter_binding_input .

?parameter_binding_input <http://odahub.io/ontology#parameter_name> ?parameter_binding_input_name ;
	a ?parameter_binding_input_type ;
	<http://odahub.io/ontology#value> ?parameter_binding_input_value ;
	<http://odahub.io/ontology#isAlso> ?parameter_binding_input_type .

?activity <http://www.w3.org/ns/oa#calls> ?run .

?run <http://odahub.io/ontology#isRequestingAstroObject> ?a_object ;
        <http://odahub.io/ontology#isRequestingAstroRegion> ?a_region ;
        <http://odahub.io/ontology#isRequestingAstroImage> ?a_image ;
        <http://purl.org/dc/terms/title> ?run_title ;
        <http://odahub.io/ontology#isUsing> ?aq_module ;
        a ?run_rdf_type .

?aq_module <https://odahub.io/ontology#AQModule> ?aq_module_name ;
	a ?aq_mod_rdf_type ;
	<http://odahub.io/ontology#isAlso> ?aq_mod_rdf_type .

?a_object <https://odahub.io/ontology#AstroObject> ?a_object_name ;
	a ?a_obj_rdf_type ;
	<http://odahub.io/ontology#isAlso> ?a_obj_rdf_type .

?a_region a ?a_region_type ; 
	<http://purl.org/dc/terms/title> ?a_region_name ;
	<http://odahub.io/ontology#isUsingSkyCoordinates> ?a_sky_coordinates ;
	<http://odahub.io/ontology#isUsingRadius> ?a_radius .

?a_image a ?a_image_type ;
	<http://purl.org/dc/terms/title> ?a_image_name ;
	<http://odahub.io/ontology#isUsingCoordinates> ?a_coordinates ;
	<http://odahub.io/ontology#isUsingPosition> ?a_position ;
	<http://odahub.io/ontology#isUsingRadius> ?a_radius ;
	<http://odahub.io/ontology#isUsingPixels> ?a_pixels ;
	<http://odahub.io/ontology#isUsingImageBand> ?a_image_band .

?a_pixels a ?a_pixels_type ;
	<http://purl.org/dc/terms/title> ?a_pixels_name .

?a_image_band a ?a_image_band_type ;
	<http://purl.org/dc/terms/title> ?a_image_band_name .

?a_coordinates a ?a_coordinates_type ;
	<http://purl.org/dc/terms/title> ?a_coordinates_name .

?a_sky_coordinates a ?a_sky_coordinates_type ;
	<http://purl.org/dc/terms/title> ?a_sky_coordinates_name .

?a_position a ?a_position_type ;
	<http://purl.org/dc/terms/title> ?a_position_name .

?a_radius a ?a_radius_type ;
	<http://purl.org/dc/terms/title> ?a_radius_name .

oda.query_where:
{
	OPTIONAL { ?parameter_binding_ontology_class a <http://www.w3.org/2002/07/owl#Class> . }
	OPTIONAL { ?parameter_binding_ontology_class <http://www.w3.org/2000/01/rdf-schema#subClassOf> ?parameter_binding_ontology_parent_class . }
	OPTIONAL { ?parameter_binding_ontology_class <http://www.w3.org/2000/01/rdf-schema#label> ?parameter_binding_ontology_class_label . }

	BIND(STRAFTER(STR(?parameter_binding_ontology_class), "#") AS ?parameter_binding_ontology_class_extracted_label) .
}
UNION
{	
	?workflow_input <http://odahub.io/ontology#expects> ?parameter_binding_input ;
		<http://odahub.io/ontology#entity_checksum> ?entityInputChecksum ;
		a <http://odahub.io/ontology#workflow> .

	?parameter_binding_input a ?parameter_binding_input_type .

	BIND(STRAFTER(STR(?parameter_binding_input), "#") AS ?parameter_binding_input_name) .

	OPTIONAL { ?parameter_binding_input_type a <http://www.w3.org/2002/07/owl#Class> . }
	OPTIONAL { ?parameter_binding_input <http://odahub.io/ontology#value> ?parameter_binding_input_value . }
}
UNION
{	
	?workflow_output <http://odahub.io/ontology#expects> ?parameter_binding_output ;
		<http://odahub.io/ontology#outputs> ?output_binding_output ;
		<http://odahub.io/ontology#entity_checksum> ?entityOutputChecksum ;
		a <http://odahub.io/ontology#workflow> .

	?parameter_binding_output a ?parameter_binding_output_type .
	?output_binding_output a ?output_binding_output_type .

	BIND(STRAFTER(STR(?parameter_binding_output), "#") AS ?parameter_binding_output_name) .
	BIND(STRAFTER(STR(?output_binding_output), "#") AS ?output_binding_output_name) .

	OPTIONAL { ?parameter_binding_output_type a <http://www.w3.org/2002/07/owl#Class> . }
	OPTIONAL { ?parameter_binding_equivalent_output_type <http://www.w3.org/2000/01/rdf-schema#equivalentClass> ?parameter_binding_output_type . }
	OPTIONAL { ?parameter_binding_output <http://odahub.io/ontology#value> ?parameter_binding_output_value . }
}
UNION
{	
	?run <http://odahub.io/ontology#isUsing> ?aq_module ;
	    <http://odahub.io/ontology#isRequestingAstroObject> ?a_object ;
	    a ?run_rdf_type ;
	    ^<http://www.w3.org/ns/oa#hasBody>/<http://www.w3.org/ns/oa#hasTarget> ?activity .

        ?aq_module <http://purl.org/dc/terms/title> ?aq_module_name ;
	    a ?aq_mod_rdf_type .

	?a_object <http://purl.org/dc/terms/title> ?a_object_name ;
           a ?a_obj_rdf_type .

	OPTIONAL { ?run <http://purl.org/dc/terms/title> ?run_title . } .

	?run ?p ?o .

	FILTER(!REGEX(STR(?a_object), "\\s")) .

	OPTIONAL {
	  ?aq_type a <http://www.w3.org/2002/07/owl#Class> .
	  FILTER (?aq_type IN (?aq_mod_rdf_type, ?a_obj_rdf_type))
	}
}
UNION
{
	?run <http://odahub.io/ontology#isUsing> ?aq_module ;
	    <http://odahub.io/ontology#isRequestingAstroRegion> ?a_region ;
	    a ?run_rdf_type ;
	    ^<http://www.w3.org/ns/oa#hasBody>/<http://www.w3.org/ns/oa#hasTarget> ?activity .

	?aq_module a ?aq_mod_rdf_type ;
	    <http://purl.org/dc/terms/title> ?aq_module_name .

	?a_region a ?a_region_type ; 
	    <http://purl.org/dc/terms/title> ?a_region_name ;
	    <http://odahub.io/ontology#isUsingSkyCoordinates> ?a_sky_coordinates ;
	    <http://odahub.io/ontology#isUsingRadius> ?a_radius .

	?a_sky_coordinates a ?a_sky_coordinates_type ;
	    <http://purl.org/dc/terms/title> ?a_sky_coordinates_name .

	?a_radius a ?a_radius_type ;
	    <http://purl.org/dc/terms/title> ?a_radius_name .

	OPTIONAL { ?run <http://purl.org/dc/terms/title> ?run_title . } .

	?run ?p ?o .

	OPTIONAL {
	  ?aq_type a <http://www.w3.org/2002/07/owl#Class> .
	  FILTER (?aq_type IN (?aq_mod_rdf_type, ?a_region_type, ?a_sky_coordinates_type, ?a_radius_type))
	}

}
UNION
{
	?run <http://odahub.io/ontology#isUsing> ?aq_module ;
	    <http://odahub.io/ontology#isRequestingAstroImage> ?a_image ;
	    a ?run_rdf_type ;
	    ^<http://www.w3.org/ns/oa#hasBody>/<http://www.w3.org/ns/oa#hasTarget> ?activity .

	?aq_module a ?aq_mod_rdf_type ;
	    <http://purl.org/dc/terms/title> ?aq_module_name .

	?a_image a ?a_image_type ;
	    <http://purl.org/dc/terms/title> ?a_image_name ;

	OPTIONAL { ?a_image <http://odahub.io/ontology#isUsingCoordinates> ?a_coordinates .
	    ?a_coordinates a ?a_coordinates_type ;
		<http://purl.org/dc/terms/title> ?a_coordinates_name .
	}
	OPTIONAL { ?a_image <http://odahub.io/ontology#isUsingPosition> ?a_position .
	    ?a_position a ?a_position_type ;
		<http://purl.org/dc/terms/title> ?a_position_name .
	}
	OPTIONAL { ?a_image <http://odahub.io/ontology#isUsingRadius> ?a_radius .
	    ?a_radius a ?a_radius_type ;
		<http://purl.org/dc/terms/title> ?a_radius_name .
	}
	OPTIONAL { ?a_image <http://odahub.io/ontology#isUsingPixels> ?a_pixels .
	    ?a_pixels a ?a_pixels_type ;
		<http://purl.org/dc/terms/title> ?a_pixels_name .
	}
	OPTIONAL { ?a_image <http://odahub.io/ontology#isUsingImageBand> ?a_image_band .
	    ?a_image_band a ?a_image_band_type ;
		<http://purl.org/dc/terms/title> ?a_image_band_name .
	}

	OPTIONAL { ?run <http://purl.org/dc/terms/title> ?run_title . } .

	?run ?p ?o .

	OPTIONAL {
	  ?aq_type a <http://www.w3.org/2002/07/owl#Class> .
	  FILTER (?aq_type IN (?a_coordinates_type, ?a_position_type, ?a_radius_type, ?a_pixels_type, ?a_image_band_type))
	}

}
